INSERT INTO REALISATEUR Values ("Steven Spielberg", "1946-12-18");
INSERT INTO FILMS (Titre, AnneeRea, Genre, Studio, Synopsis, Realisateur) VALUES ("Les Dents de la mer", 1975, "Drame", "Cic", "A quelques jours du début de la saison estivale, les habitants de la petite station balnéaire d'Amity sont mis en émoi par la découverte sur le littoral du corps atrocement mutilé d'une jeune vacancière. Pour Martin Brody, le chef de la police, il ne fait aucun doute que la jeune fille a été victime d'un requin. Il décide alors d'interdire l'accès des plages mais se heurte à l'hostilité du maire uniquement intéressé par l'afflux des touristes. Pendant ce temps, le requin continue à semer la terreur le long des côtes et à dévorer les baigneurs...", "Steven Spielberg");
INSERT INTO FILMS (Titre, AnneeRea, Genre, Studio, Synopsis, Realisateur) VALUES ("Django Unchained", 2013, "Western", "Sony", "Dans le sud des États-Unis, deux ans avant la guerre de Sécession, le Dr King Schultz, un chasseur de primes allemand, fait l’acquisition de Django, un esclave qui peut l’aider à traquer les frères Brittle, les meurtriers qu’il recherche. Schultz promet à Django de lui rendre sa liberté lorsqu’il aura capturé les Brittle – morts ou vifs.
